import base64

# 서명 base64 데이터 (data:image/png;base64,... 전체 문자열)
base64_string = 'data:image/png;base64,'
# "data:image/png;base64," 접두사 제거
header, encoded = base64_string.split(",", 1)

# base64 디코딩
binary_data = base64.b64decode(encoded)

# 파일로 저장
with open("signature.jpg", "wb") as f:
    f.write(binary_data)

print("서명 이미지가 signature.png로 저장되었습니다.")
